    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        Product selectedProduct = StockDialog.getSelectedProduct();
        
        labelName.setText(selectedProduct.getName());
        labelVolume.setText(String.valueOf(selectedProduct.getVolume()));
        labelPrice.setText(String.valueOf(selectedProduct.getPrice()));
        labelAmount.setText(String.valueOf(selectedProduct.getAmount()));
        labelMargin.setText(String.valueOf(selectedProduct.getMargin()));
    }//GEN-LAST:event_formWindowOpened

